from __future__ import absolute_importfrom celery import Celeryapp = Celery('celery_in_project',             broker='amqp://guest@localhost//',             # backend='amqp://guest@localhost//',             backend='redis://localhost',             include=['celery_in_project.tasks']             )app.conf.update(    CELERY_TASK_SERIALIZER='json',    CELERY_RESULT_SERIALIZER='json',    CELERY_ACCEPT_CONTENT=['json'],    CELERY_TIMEZONE='Asia/Shanghai',    CELERY_ENABLE_UTC=True)